<div class="search-bar">
	<input type="text" bind:value={indirect_value} placeholder="Search . . ."/>
</div>

<script>
	export let value
	let indirect_value = ''

	let interval
	$: if (indirect_value.trim()) {
		clearTimeout(interval)
		interval = setTimeout(() => {
			value = encodeURIComponent(indirect_value.trim())
		}, 250)
	} else {
		value = ''
	}
</script>

<style type="text/scss">
	input {
		padding: 0 10rem;
		height: 34rem;
		width: 100%;
		margin: 0;
		background-color: white;
		border: 1px solid #cdccd0;
		border-radius: 0;
		line-height: 34rem;
		&:hover,
		&:focus {
			border-color: $black;
		}
	}
</style>
