<div class="toolbar full-width layout-outer">
	<div class="layout-inner">
		<div class="boxes">
			<div class="box">
				<SelectBox
					title="Content Type"
					on:change={() => changed('type', contentValue)}
					primary="All Content Types"
					bind:value={contentValue}
					options={contentOptions}/>
				<!-- {contentValue || typeof contentValue} -->
			</div>
			<div class="box">
				<SelectBox
					title="Decade"
					on:change={() => changed('decade', decadeValue)}
					primary="All Decades"
					bind:value={decadeValue}
					options={decadeOptions}/>
				<!-- {decadeValue || typeof decadeValue} -->
			</div>
		</div>
	</div>
</div>

<script>
	import SelectBox from '@johnny/svelte/SelectBox.svelte'
	// TODO: preload this from the backend
	const contentOptions = ['Press', 'Document', 'Item', 'Advertisement', 'Photo']
	// TODO: preload this from the backend
	const decadeOptions = ['1950s', '1960s', '1970s', '1980s', '1990s', '2000s', '2010s']
	export let contentValue
	export let decadeValue

	import { createEventDispatcher } from 'svelte'
	const dispatch = createEventDispatcher()
	const changed = (key, value) => dispatch('filter', { key, value })
</script>

