export const createWhere = ({ status = [], tags = [] }) => {
	let where = ''
	if (status.length || tags.length) {
		where = 'where: { AND: ['
		if (status.length) {
			where += '{ OR: ['
			where += status.map(stat => `{ status: ${stat} }`).join(' ')
			where += '] }'
		}
		where += tags.length ? tags.map(tag => `{ tags_some: { tag: "${tag}" } }`).join(' ') : ''
		where += '] }'
	}
	// console.log(where)
	return where
}

const decade_mapper = {
	Prologue: 'Prologue',
	The1950s: '1950s',
	The1960s: '1960s',
	The1970s: '1970s',
	The1980s: '1980s',
	The1990s: '1990s',
	The2000s: '2000s',
	Today: 'Today',
}
export const convert_decades = items => (items.map(item => {
	item.decade = decade_mapper[item.decade] || null
	return item
}))
